w,h = 80,55
r = 320 // w

def fill_rect(x,y,w,h,c):
  for i in range(w+1):
    for j in range(h+1):
      set_pixel(x+i,y+j,c)
  show_screen()
  return

def taille(n):
  return  54 * (n[0] - 35) + n[1] - 34

def remplir(x,y,t,c):
  while t > 0:
    d = min(t, w - x)
    fill_rect(r*x,2+r*y,r*d,r-2,c)
    x += d
    t -= d
    if x == w:
      x = 0
      y += 1
      if y >= h:
        y = 0
#  show_screen()
  return x,y   
       
def aff():
  k, sens = 0, 1
  while True:
    k = (k + sens) % 50
    if random()<.01:
      sens *= -1
    img = memoire(k)    
    COUL = [(randint(0,255),randint(0,255),randint(0,255)),(0,0,0)]
#    COUL = [lcd.WHITE,lcd.BLACK]
    i = 0
    x,y = 0,0
    while i < len(img):
      v = img[i] 
      if v == 32 or v == 33:
        c = v - 32
        i += 1
#        sleep(.02)
      else:
        t = taille(img[i:i+2])
        x,y = remplir(x,y,t,COUL[c])
        i += 2
        c = 1 - c

clear_screen()
aff()
